mapper.select:
select

from activity_new_month_info
WHERE 1=1

  
  AND phone LIKE #{phone}


  
    AND month_start_date > DATE_FORMAT(now(),'%Y-%m-%d')
  
  
    AND month_start_date <= DATE_FORMAT(now(),'%Y-%m-%d') AND month_end_date >= DATE_FORMAT(now(),'%Y-%m-%d')
  
  
    AND month_end_date < DATE_FORMAT(now(),'%Y-%m-%d')
  


  AND online_date >= #{onLineStartDate}


  AND online_date <= #{onLineEndDate}


  AND month_start_date >= #{monthStartBeginDate}


  AND month_start_date <= #{monthStartEndDate}


  AND month_end_date >= #{monthEndbeginDate}


  AND month_end_date <= #{monthEndFinishDate}

order by create_date DESC

  limit #{pageNum},#{pageSize}

select
count(id)
from activity_new_month_info
WHERE 1=1

  
  AND phone LIKE #{phone}


  
    AND month_start_date > DATE_FORMAT(now(),'%Y-%m-%d')
  
  
    AND month_start_date <= DATE_FORMAT(now(),'%Y-%m-%d') AND month_end_date >= DATE_FORMAT(now(),'%Y-%m-%d')
  
  
    AND month_end_date < DATE_FORMAT(now(),'%Y-%m-%d')
  


  AND online_date >= #{onLineStartDate}


  AND online_date <= #{onLineEndDate}


  AND month_start_date >= #{monthStartBeginDate}


  AND month_start_date <= #{monthStartEndDate}


  AND month_end_date >= #{monthEndbeginDate}


  AND month_end_date <= #{monthEndFinishDate}

mapper.insert:
insert into activity_new_month_info (id, phone, city, 
  online_date, month_start_date, month_end_date, 
  month_status, is_del, create_date
  )
values (#{id,jdbcType=INTEGER}, #{phone,jdbcType=VARCHAR}, #{city,jdbcType=VARCHAR}, 
  #{onlineDate,jdbcType=TIMESTAMP}, #{monthStartDate,jdbcType=TIMESTAMP}, #{monthEndDate,jdbcType=TIMESTAMP}, 
  #{monthStatus,jdbcType=VARCHAR}, #{isDel,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}
  )
