SELECT ID, STATION_NAME, SERVICE_CHARGE_AMOUNT, BASIC_CHARGE_AMOUNT, PERSON_SERVICE_AMOUNT, THIRD_SERVICE_AMOUNT, CREATE_TIME, PROVINCE, CITY, REGION, COORD, IS_DIRECT, DIRECT_BASIC_CHARGE_AMOUNT, DIRECT_PERSON_SERVICE_AMOUNT, DIRECT_SERVICE_CHARGE_AMOUNT, DIRECT_THIRD_SERVICE_AMOUNT, IS_FORMAL, CASE WHEN IS_DIRECT = 0 AND `STATUS` != 2 THEN 1 WHEN IS_DIRECT = 1 AND `STATUS` != 2 THEN 2 WHEN IS_DIRECT = 0 AND `STATUS` = 2 THEN 3 WHEN IS_DIRECT = 1 AND `STATUS` = 2 THEN 4 ELSE 0 END `STATUS` FROM elec_station
1=1 and STATUS != 0
and PROVINCE = #{province}
and CITY like #{city}
and (((SERVICE_CHARGE_AMOUNT+BASIC_CHARGE_AMOUNT) < #{price}+0.1 and (SERVICE_CHARGE_AMOUNT+BASIC_CHARGE_AMOUNT) >= #{price}) or ((DIRECT_BASIC_CHARGE_AMOUNT+DIRECT_SERVICE_CHARGE_AMOUNT) < #{price}+0.1 and (DIRECT_BASIC_CHARGE_AMOUNT+DIRECT_SERVICE_CHARGE_AMOUNT) >= #{price}))
and REGION like #{region}

  	SELECT
		MAX(
			BASIC_CHARGE_AMOUNT + SERVICE_CHARGE_AMOUNT
		) AS maxPrice,
		MIN(
			BASIC_CHARGE_AMOUNT + SERVICE_CHARGE_AMOUNT
		) AS minPrice,
		MAX(
			DIRECT_SERVICE_CHARGE_AMOUNT + DIRECT_BASIC_CHARGE_AMOUNT
		) AS maxDirectPrice,
		MIN(
			DIRECT_SERVICE_CHARGE_AMOUNT + DIRECT_BASIC_CHARGE_AMOUNT
		) AS minDirectPrice
	FROM
		elec_station

		1=1
		and STATUS != 0 

			and PROVINCE = #{province}



			and CITY like #{city}



			and REGION like #{region}

