mapper.select:
select a.id,a.user_phone,s.PERSON_PHONE,s.STATION_NAME,a.create_date from elec_user_append a,elec_station s where a.station_id=s.id
and s.STATION_NAME like #{append.stationName}
and s.PERSON_PHONE = #{append.userAccount}
and a.user_phone = #{append.userPhone}
and a.create_date >=DATE_FORMAT(#{append.startDate},'%Y-%m-%d 00:00:00')
and a.create_date <= DATE_FORMAT(#{append.endDate},'%Y-%m-%d 23:59:59')
order by a.create_date DESC
limit #{append.pageNum,jdbcType=INTEGER},#{append.pageSize,jdbcType=INTEGER}

mapper.update:
update elec_user_append
set user_phone = #{userPhone,jdbcType=VARCHAR},
  station_id = #{stationId,jdbcType=INTEGER},
  create_date = #{createDate,jdbcType=TIMESTAMP},
  id_del = #{idDel,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
